import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { ReactiveFormsModule } from '@angular/forms';
import { NgModule } from '@angular/core';


@NgModule({
  imports: [
    // Other imports
    ReactiveFormsModule,
  ],
  // Other module configurations
})
export class SignupModule { }


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {
    constructor(private router: Router){

      this.myForm = new FormGroup({
        name: new FormControl('', Validators.required),
        email: new FormControl('', [Validators.required, Validators.email]),
        // Add more form controls as needed
      });
    }
    submit(){
      this.router.navigate(['/login'])
    }
    home2(){
      this.router.navigate(['/home'])

    }
    myForm: FormGroup;


submitForm() {
  if (this.myForm.valid) {
    // Form is valid, perform desired actions (e.g., API call, navigation)
    console.log('Form submitted');
  } else {
    // Form is invalid, display error messages or handle appropriately
    console.log('Invalid form');
  }
}
}
<!DOCTYPE html>
<html>
  <style>
    h2{
      text-align: center;
    }


  </style>
<body>
  <input type="submit" value="HOME" (click)="home2()">
<h2>SIGN UP Page</h2>

<form class="c" [formGroup]="myForm" (ngSubmit)="submitForm()">

  <div>
    <label for="name">Name:</label>
    <input type="text" id="name" formControlName="name">

    <label for="email">Email:</label>
    <input type="email" id="email" formControlName="email">

    <!-- Add more form controls as needed -->

    <button type="submit" [disabled]="myForm.invalid" (click)="submit()">Submit</button>

</div>
<div *ngIf="myForm.controls.name.errors && myForm.controls.name.touched">
  <span class="error-message">Name is required</span>
</div>
<div *ngIf="myForm.controls.email.errors && myForm.controls.email.touched">
  <span class="error-message">Please enter a valid email address</span>
</div>

</form> 



</body>
</html>
